export class Artifact {
    type: string;
    image: string;
    dockerfileUrl: string;
    helmchartUrls: string[];
    manifestUrls: string[];
}

export class TargetResource {
    type: string;
    uri: string;
    name: string;
    properties: any;
}

export class WorkflowRun {
    provider: string;
    workflow: Workflow;
    runUri: string;
    createdBy: string;
    jobName: string;
    creationTimestamp: string;
    modifiedTimestamp: string;
    status: string;
    commit: string;
}

export class Workflow {
    repository: string;
    repositoryId: string;
    name: string;
    path: string;
    ref: string;
}