export class Artifact {
    type: string;
    properties: any;
}

export class Pipeline {
    provider: string;
    properties: GitHubPipelineProperties | any;
}

export class GitHubPipelineProperties {
    repository: string;
    workflow: string;
    run: string;
    runUri: string;
    createdBy: string;
    branch: string;
    jobName: string;
    creationTimestamp: string;
    modifiedTimestamp: string;
    status: string;
    commit: string;
}

export class TargetResource {
    type: string;
    uri: string;
    name: string;
    properties: any;
}